sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	//var cdt = g_form.getValue('due_date'); //first Date/Time field
	//var dttype = 'minute'; //this can be day, hour, minute, second. By default it will return seconds.
	g_form.clearMessages();
	var ajax1 = new GlideAjax('ClientDateUtil');
	ajax1.addParam('sysparm_name','getBeforeDate');
	ajax1.addParam('sysparm_fdt', newValue);
	ajax1.getXML(doSomething);

	function doSomething(response){
		var answer = response.responseXML.documentElement.getAttribute("answer");
		//alert(typeof answer);
		var isTrueSet = (answer === 'true');
		if (isTrueSet){
			g_form.addErrorMessage("Date can not be set in Past");
			g_form.clearValue("u_when_needed");
		}
		else{
			jslog("All good");
		}
	}
}
