sys_script_include.script:
var Employee = Class.create();
Employee.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getManagedEmployees: function(managerID , arrEmployees){
		var usr = new GlideRecord('sys_user');
		usr.addQuery('manager', managerID);
		usr.addActiveQuery();
		usr.query();
		usr.getRowCount();

		while(usr.next()){
			arrEmployees.push(""+ usr.sys_id);
			//gs.addErrorMessage(usr.sys_id);
			arrEmployees.join(this.getManagedEmployees(usr.sys_id, arrEmployees));
		}
		return arrEmployees;
	},
    type: 'Employee'
});
